from tqdm import tqdm
import libnum

myall = []

for k in tqdm(range(1, 1001)):
    a = [1, k]
    for i in range(k):
        a.append(k * a[-1] - a[-2])
    a = a[:k+1]
    cur_c = [x + y for x, y in zip(a[:-1], a[1:])]
    myall.extend(cur_c)

myall.sort()
C_321123 = myall[321123]

def get_hint(C_321123):
    '''
    solve it and you can get my hint!
    '''
    
    hint=(C_321123)//(520**100*5201314**47)-substraction   #C_321123代表第321123项
    hint//=(520*5201314)
    return hint
    
hint = get_hint(C_321123)
print(libnum.n2s(hint))
